import numpy as nparray = np.array([1, 2, 7, 5, 2, 8, 3, 4, 2, 2, 5, 3, 4, 1, 6, 7, 8, 6])density = array.reshape((3, 3, 2))print(density)x=3y=3z=2output_file = 'toy-data-2.txt'file_PD = open(output_file, 'a')file_PD.write("3\n")for i in range(0, z):    for j in range(0, y):        for k in range(0, x):            new = '0 '+str(k)+' '+str(j) + ' '+ str(i) + ' ' + str(density[k, j, i])            file_PD.write(new+"\n")            if k+1<x:                new = '1 '+str(k)+' '+str(j) + ' '+ str(i) + ' '+str(k+1) + ' '+str(j) + ' '+ str(i) + ' '+ str(max(density[k, j, i], density[k+1, j, i]))                file_PD.write(new+"\n")            if j+1<y:                new = '1 '+str(k)+' '+str(j) + ' '+ str(i) + ' '+str(k) + ' '+str(j+1) + ' '+ str(i) + ' '+ str(max(density[k, j, i], density[k, j+1, i]))                file_PD.write(new+"\n")            if i+1<z:                new = '1 '+str(k)+' '+str(j) + ' '+ str(i) + ' '+str(k) + ' '+str(j) + ' '+ str(i+1) + ' ' + str(max(density[k, j, i], density[k, j, i+1]))                file_PD.write(new+"\n")file_PD.close()